function data(){$(document).ready(function(){$.getJSON("data.json",function(e){switch(e.lang){case"en":english(e);break;case"es":spanish(e)}if(0==e.experience.length)for(i=0;i<document.getElementsByClassName("display-experience").length;i++)document.getElementsByClassName("display-experience")[i].style.display="none";else experience(e);if(0==e.education.length)for(i=0;i<document.getElementsByClassName("display-education").length;i++)document.getElementsByClassName("display-education")[i].style.display="none";else education(e);social(e),document.documentElement.style.setProperty("--color",e.color),document.title=e.name+" "+e.lastname,document.description=e.description,document.getElementById("navname").innerText=e.name+" "+e.lastname,document.getElementById("name").innerText=e.name,document.getElementById("lastname").innerText=e.lastname,document.getElementById("address").innerText=e.address,document.getElementById("city").innerText=e.city,document.getElementById("phone").innerText=e.phone,document.getElementById("mail").innerText=e.mail,document.getElementById("description").innerText=e.description})})}function experience(e){for(i=0;i<e.experience.length;i++)$("#expContent").append("<div class='resume-item d-flex flex-column flex-md-row justify-content-between mb-5'> <div class='resume-content'> <h3 class='mb-0'>"+e.experience[i].position+"</h3> <div class='subheading mb-3'>"+e.experience[i].companyname+"</div><p>"+e.experience[i].description+"</p></div><div class='resume-date text-md-right'> <span class='text-primary'>"+e.experience[i].datein+" - "+e.experience[i].dateout+"</span> </div></div>")}function education(e){for(i=0;i<e.education.length;i++)$("#eduContent").append("<div class='resume-item d-flex flex-column flex-md-row justify-content-between mb-5'> <div class='resume-content'> <h3 class='mb-0'>"+e.education[i].schoolname+"</h3> <div class='subheading mb-3'>"+e.education[i].degree+"</div><div>"+e.education[i].description+"</div></div><div class='resume-date text-md-right'> <span class='text-primary'>"+e.education[i].datein+" - "+e.education[i].dateout+"</span> </div>")}function social(e){""==e.social.linkedin?document.getElementById("linkedin").style.display="none":document.getElementById("linkedin").href=e.social.linkedin,""==e.social.github?document.getElementById("github").style.display="none":document.getElementById("github").href=e.social.github,""==e.social.twitter?document.getElementById("twitter").style.display="none":document.getElementById("twitter").href=e.social.twitter,""==e.social.facebook?document.getElementById("facebook").style.display="none":document.getElementById("facebook").href=e.social.facebook}function english(e){document.getElementsByClassName("nav1")[0].textContent="About",document.getElementsByClassName("nav2")[0].textContent="Experience",document.getElementsByClassName("nav2")[1].textContent="Experience",document.getElementsByClassName("nav3")[0].textContent="Education",document.getElementsByClassName("nav3")[1].textContent="Education"}function spanish(e){document.getElementsByClassName("nav1")[0].textContent="Sobre mí",document.getElementsByClassName("nav2")[0].textContent="Experiencia",document.getElementsByClassName("nav2")[1].textContent="Experiencia",document.getElementsByClassName("nav3")[0].textContent="Formación",document.getElementsByClassName("nav3")[1].textContent="Formación"}
